import React, { FormEvent, useRef, VFC } from 'react';

import Input from './../../components/input';
import Button from './../../components/button';

const Register: VFC = () => {
  const nameRef = useRef<HTMLInputElement>(null);
  const emailRef = useRef<HTMLInputElement>(null);
  const PasswordRef = useRef<HTMLInputElement>(null);

  const onSubmit = (event: FormEvent) => {
    if (event) event.preventDefault();

    const [
      name,
      email,
      password
    ] = [nameRef, emailRef, PasswordRef].map(ref => ref.current?.value);

    console.log(name, email, password);

  }
  return (
    <section className="register__contaner">
      <section className="register__content">
        <h2>Register</h2>

        <form onSubmit={onSubmit}>
              <Input name='name' placeholder='Enter name' ref={nameRef}/>
              <Input name='email' placeholder='Enter email' ref={emailRef}/>
              <Input name='password' placeholder='Enter password' ref={PasswordRef}/>

              <Button>
                Register
              </Button>
        </form>
      </section>
    </section>
  )
};

export default Register;
