import Context from "./context";

export type GameStore = {
  moves: number;
  time: number;
  fieldSize: {
    width: number;
    height: number;
  };
  field: string[][]
};

export const initialGameState: GameStore = {
  moves: 0,
  time: 0,
  fieldSize: {
    width: 0,
    height: 0
  },
  field: []
};

const GameContext = new Context(initialGameState);

export default GameContext;
