import { useEffect, useLayoutEffect, VFC } from "react";
import { useNavigate } from "react-router";
import { Link } from "react-router-dom";
import StatsService from "../../api/services/stats";
import AppContext from "../../store/context/app";
import StatsContext from "../../store/context/stats";

const Main: VFC = () => {
  const { data: { user: { _id: id, name } } } = AppContext.useContext();
  const { data, setData: setStatsData } = StatsContext.useContext();

  const navigate = useNavigate();

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    console.log(userId);

    if (localStorage.getItem('AuthToken') && userId) {
      StatsService.get(userId)
        .then(res => {
          console.log(res.data);

          setStatsData(res.data);
        })
        .catch((err) => {
          navigate('/login');
        });
    } else {
      navigate('/login');
    }
  }, [id, localStorage.getItem('userId'), localStorage.getItem('AuthToken')]);

  return (
    <main>
      <h1>Hello, { name }</h1>

      <nav>
        <Link to='/profile'>Profile</Link>
      </nav>
    </main>
  )
};

export default Main;
