import React, { FormEvent, useRef, VFC } from 'react';

import Input from './../../components/input';
import Button from './../../components/button';
import AuthService from '../../api/services/auth';
import AppContext from '../../store/context/app';
import { useNavigate } from 'react-router';
import { Link } from 'react-router-dom';

const Register: VFC = () => {
  const nameRef = useRef<HTMLInputElement>(null);
  const emailRef = useRef<HTMLInputElement>(null);
  const PasswordRef = useRef<HTMLInputElement>(null);

  const { data: { isLoading }, setData: setAppData } = AppContext.useContext();

  const onSubmit = (event: FormEvent) => {
    if (event) event.preventDefault();

    const [
      name,
      email,
      password
    ] = [nameRef, emailRef, PasswordRef].map(ref => ref.current?.value);

    console.log(name, email, password);

    setAppData({ isLoading: true });

    AuthService.register({
      name: name!,
      email: email!,
      password: password!
    })
    .then(res => {
      const { token, user } = res.data;

      setAppData({
        user,
        token
      });
    })
    .catch(err => {
      console.log(err);

    })
    .finally(() => setAppData({ isLoading: false }));
  }
  return (
    <section className="register__contaner">
      <section className="register__content">
        <h2>Register</h2>

        <form onSubmit={onSubmit}>
          <Input name='name' placeholder='Enter name' ref={nameRef}/>
          <Input name='email' placeholder='Enter email' ref={emailRef}/>
          <Input name='password' placeholder='Enter password' ref={PasswordRef}/>

          <Button disabled={true}>
            Register
          </Button>
        </form>

        <section>
            Not registered?
            <Link to='/login'>Login</Link>
        </section>
      </section>
    </section>
  )
};

export default Register;
