import { ChangeEvent, useEffect, useRef, useState, VFC } from "react";
import { useNavigate } from "react-router";

import AuthService from "../../api/services/auth";
import StatsService from "../../api/services/stats";

import Button from "../../components/button";
import Input from "../../components/input";
import Navigation from "../../components/navbar";
import Timer from "../../components/timer";

import AppContext from "../../store/context/app";
import GameContext from "../../store/context/game";
import StatsContext from "../../store/context/stats";
import { Difficulty, Size } from "../../utils";

import Game from "../game";

const Main: VFC = () => {
  const { data: {
    user: { _id: id, name },
    token,
    difficulty
  }, setData: setAppData } = AppContext.useContext();
  const { setData: setStatsData } = StatsContext.useContext();
  const { setData: setGameData } = GameContext.useContext();

  const widthRef = useRef<HTMLInputElement>(null);
  const heightRef = useRef<HTMLInputElement>(null);

  const [isStarted, setIsStarted] = useState(false);
  const [isFinished, setIsFinished] = useState(false);

  const onDifficultyChange = (event: ChangeEvent<HTMLInputElement>) => {
    setAppData({ difficulty: event.target.value });
  }

  const onFinishGame = () => {
    setIsFinished(true);
    setIsStarted(false);
  }

  const onStartGame = () => {
    const [
      width,
      height
    ] = [widthRef, heightRef].map(ref => ref.current?.value || 0);

    if (!width || !height || Number(width) < 4 || Number(height) < 4) {
      return alert('Enter width and height mnimum 4!!');
    }

    const fieldSize: Size = {
      width: Number(width),
      height: Number(height)
    }

    setGameData({ fieldSize });

    setIsStarted(true);
  }

  const navigate = useNavigate();

  useEffect(() => {
    const userId = id || localStorage.getItem('userId');

    AuthService.getUser(userId!)
      .then(res => {
        const { user, stats } = res.data;

        setAppData({ user });
        setStatsData({ ...stats });
      })
      .catch((err) => {
        navigate('/login');
      });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [id, token]);

  return (
    <main className='main__container container'>
      <Navigation />

      {isStarted ? null : (
        <h1>Hello, {name}</h1>
      )}

      <section>

        <section >
          {isStarted ? (
            <section>
              <Timer isTicking={!isFinished} />
              <Game onFinish={onFinishGame} />
              <Button onClick={onFinishGame}>
                Finish Game
              </Button>
            </section>
          ) : (
            <section>
              <h4>set the field size</h4>
              <section>
                <label htmlFor="width">
                  <p>Width</p>
                  <Input type="number" name="width" ref={widthRef} />
                </label>

                <label htmlFor="height">
                  <p>height</p>
                  <Input type="number" name="width" ref={heightRef} />
                </label>
              </section>

              <section>
                <h4>Difficulty</h4>

                <input
                  onChange={onDifficultyChange}
                  checked={difficulty === Difficulty.easy}
                  type="radio"
                  name="defficulty"
                  value="easy"
                  id="easy"
                />
                <label htmlFor="easy">Easy</label>

                <input
                  onChange={onDifficultyChange}
                  checked={difficulty === Difficulty.middle}
                  type="radio"
                  id="middle"
                  name="defficulty"
                  value="middle"
                />
                <label htmlFor="middle">Middle</label>
              </section>

              <Button onClick={onStartGame}>
                Start Game
              </Button>
            </section>
          )}
        </section>
      </section>
    </main>
  )
};

export default Main;
