import { useEffect, useLayoutEffect, useRef, useState, VFC } from "react";
import { useNavigate } from "react-router";
import { Link } from "react-router-dom";

import AuthService from "../../api/services/auth";
import StatsService from "../../api/services/stats";

import Button from "../../components/button";
import Input from "../../components/input";
import Navigation from "../../components/navbar";

import AppContext from "../../store/context/app";
import GameContext from "../../store/context/game";
import StatsContext from "../../store/context/stats";

import Game from "../game";

const Main: VFC = () => {
  const { data: { user: { _id: id, name } }, setData: setUserData } = AppContext.useContext();
  const { data, setData: setStatsData } = StatsContext.useContext();
  const { data: gameData, setData: setGameData } = GameContext.useContext();

  const widthRef = useRef<HTMLInputElement>(null);
  const heightRef = useRef<HTMLInputElement>(null);

  const [isStarted, setIsStarted] = useState(false);

  const onStartGame = () => {
    setIsStarted(true);
  }

  const navigate = useNavigate();

  useEffect(() => {
    const userId = id || localStorage.getItem('userId');

    if (userId) {
      AuthService.getUser(userId!)
        .then(res => {
          const { user, stats } = res.data;

          setUserData({ user });
          setStatsData({ ...stats });
        })
        .catch((err) => {
          navigate('/login');
        });
    }
  }, []);

  return (
    <main>
      <Navigation />

      <h1>Hello, {name}</h1>

      <section>
        <h4>set the field size</h4>

        <section >
          {isStarted ? (
            <Game />
          ) : (
            <section>

              <section>
                <label htmlFor="width">
                  <p>Width</p>
                  <Input type="number" name="width" ref={widthRef} />
                </label>

                <label htmlFor="height">
                  <p>height</p>
                  <Input type="number" name="width" ref={heightRef} />
                </label>
              </section>

              <Button onClick={onStartGame}>
                Start Game
              </Button>
            </section>
          )}
        </section>
      </section>
    </main>
  )
};

export default Main;
