import React, { FormEvent, useEffect, useRef, VFC } from 'react';
import { Link, useNavigate } from 'react-router-dom';

import AuthService from '../../api/services/auth';

import AppContext from '../../store/context/app';

import Button from '../../components/button';
import Input from '../../components/input';

import Logo from '../../components/logo';

import './login.scss';
import SummaryContext from '../../store/context/summary';

const Login: VFC = () => {
  const emailRef = useRef<HTMLInputElement>(null);
  const PasswordRef = useRef<HTMLInputElement>(null);

  const { data: { isLoading }, setData: setAppData } = AppContext.useContext();
  const { setData: setSummaryData } = SummaryContext.useContext();

  const navigate = useNavigate();

  const onSubmit = (event: FormEvent) => {
    if (event) event.preventDefault();

    const [
      email,
      password
    ] = [emailRef, PasswordRef].map(ref => ref.current?.value);

    setAppData({ isLoading: true });

    AuthService.login({
      email: email!,
      password: password!
    })
    .then(res => {
      const { token, user } = res.data;

      setAppData({ user, token });

      localStorage.setItem('userId', user._id!);
      localStorage.setItem('AuthToken', token);
    })
    .then(() => navigate('/main'))
    .catch(err => {
      console.log(err);

      setSummaryData({
        isShow: true,
        isSuccess: false,
        title: "Something went wrong!",
        subtitle: err.response
      });
    })
    .finally(() => setAppData({ isLoading: false }));
  }

  useEffect(() => {
    localStorage.removeItem('userId');
    localStorage.removeItem('AuthToken');
  }, []);

  return (
    <section className="login__container container">
      <section className="login__content">
        <Logo />

        <h2 className='login__title'>Hello Again!</h2>

        <section className='login__register'>
          <span>Not registered?&nbsp;</span>
          <Link className='login__register--link' to='/register'>Register</Link>
        </section>

        <form onSubmit={onSubmit} className='login__form'>
          <section className='login__inputs'>
            <Input name='email' placeholder='Enter login or email' ref={emailRef} />
            <Input name='password' placeholder='Enter password' ref={PasswordRef} />
          </section>

          <section>
            <Button disabled={isLoading}>
              Login
            </Button>
          </section>

        </form>

      </section>
    </section>
  )
};

export default Login;
