import { AxiosResponse } from "axios";

import { BaseUser, RegisterUser } from '../../types';
import path from '../../constants';

import api  from './../';

export default class AuthService {
  static register = (data: RegisterUser): Promise<AxiosResponse<BaseUser>> => {
    return api.post(`${path.main}/${path.auth.register}`, data);
  }

  static login = (data: BaseUser): Promise<AxiosResponse<BaseUser>> => {
    return api.post(`${path.main}/${path.auth.login}`, data);
  }
}
