import React, { FC  } from 'react'
import AppContext from '../../store/context/app';

import Transition from 'react-transition-group/Transition';
import { CSSTransition, SwitchTransition } from 'react-transition-group';


import Logo from '../logo';

import './loading.scss';

const duration = 300;

const defaultStyle = {
  transition: `opacity ${duration}ms ease-in-out`,
  opacity: 0,
}

const transitionStyles = {
  entering: { opacity: 1 },
  entered:  { opacity: 1 },
  exiting:  { opacity: 0 },
  exited:  { opacity: 0 },
};

type LoadingProps = {
  isLoading?: boolean;
}
const Loading: FC<LoadingProps> = ({ isLoading }) => {
  const { data: { isLoading: isAppLoading } } = AppContext.useContext();

  // if (!isLoading && !isAppLoading) return null;

  // if (isLoading || isAppLoading) {
    return (
      <SwitchTransition>
        <CSSTransition
          in={isLoading || isAppLoading}
          timeout={duration}
          classNames='loading'
          unmountOnExit
        >
          {state => (
            <div className="loading">
              <div className="loading__overlay">
                <Logo />
              </div>
            </div>
          )}
        </CSSTransition>
      </SwitchTransition>
    )
  // }
};

export default Loading;
