import { FC } from "react";
import classnames from 'classnames';

import GameContext from "../../store/context/game";

import './cell.scss';

type CellProps = {
  content: string;
  column: number;
  row: number;
};

const Cell: FC<CellProps> = ({ content, row, column }) => {
  const { data: { onCellClick, field, lastTry } } = GameContext.useContext();

  let isAllowedToGo = true;

  if (lastTry.row != undefined && lastTry.column != undefined) {
    const deltaX = Math.abs(column - lastTry.column);
    const deltaY = Math.abs(row - lastTry.row);

    isAllowedToGo = Number(content) === 0 && (
      ((deltaX === 2) && (deltaY === 1)) ||
      ((deltaX === 1) && (deltaY === 2)));
  }

  return (
    <div
      className={classnames('cell__container', {
        'cell__available': isAllowedToGo
      })}
      onClick={() => isAllowedToGo ? onCellClick(row, column, field, lastTry) : {}}
    >
      {content}
    </div>
  )
};

export default Cell;
