import { useEffect, VFC } from "react";

import Row from "../../components/row";

import GameContext from "../../store/context/game";

const Game: VFC = () => {

  const { data: {
    fieldSize,
    currentNumber
  }, setData: setGameData } = GameContext.useContext();

  const onCellClick = (row: number, column: number) => {
    const fieldCopy = [...field];
    fieldCopy[row][column] = currentNumber;

    setGameData({
      field: fieldCopy,
      currentNumber: currentNumber + 1
    });
  }

  useEffect(() => {
    const field = Array(fieldSize.height)
      .fill(Array(fieldSize.width).fill('0'));

    setGameData({
      field,
      onCellClick
    });
  }, []);



  const rows = Array(fieldSize.height)
    .fill(Array(fieldSize.width).fill('0'))
    .map((row, index) => (
      <Row key={`row${index}`} content={row} number={index} />)
    );

  return (
    <section>
      {rows}
    </section>
  )
};

export default Game;
