import { useEffect, VFC } from "react";
import { useNavigate } from "react-router";
import StatsService from "../../api/services/stats";
import AppContext from "../../store/context/app";
import StatsContext from "../../store/context/stats";

const Main: VFC = () => {
  const { data: { user: { id } } } = AppContext.useContext();
  const { data, setData: setStatsData } = StatsContext.useContext();

  const navigate = useNavigate();

  useEffect(() => {
    const { _id: userId, name } = JSON.parse(localStorage.getItem('user')!);
    if (userId && name) {
      StatsService.get(userId)
        .then(res => {
          console.log(res.data);

          setStatsData(res.data);
        })
        .catch((err) => {
          navigate('/login');
        });
    } else {
      navigate('/login');
    }
  }, []);

  return (
    <h1>Main page</h1>
  )
};

export default Main;
