import { FC } from "react";
import GameContext from "../../store/context/game";

import './cell.scss';

type CellProps = {
  content: string;
  column: number;
  row: number;
};

const Cell: FC<CellProps> = ({ content, row, column }) => {
  const { data: { onCellClick, field, lastTry } } = GameContext.useContext();

  let isAllowedToGo = true;

  if (lastTry.row && lastTry.column) {
    const deltaX = Math.abs(column - lastTry.column);
    const deltaY = Math.abs(row - lastTry.row);

    isAllowedToGo = (deltaX  === 2) && (deltaY === 1);
  }


  return (
    <div className='cell__container' onClick={() => isAllowedToGo ? onCellClick(row, column, field) : {}}>
      {content}
    </div>
  )
};

export default Cell;
