import { Navigate } from "react-router-dom";
import AppContext from './../store/context/app';

const Auth = ({ children }: any) => {
  const { data: { user: { id } } } = AppContext.useContext();

  return id || localStorage.getItem('AuthToken') ? children : <Navigate to='/login'/>;
};

export default Auth;
