import { useCallback, useEffect, useMemo, VFC } from "react";

import Row from "../../components/row";

import GameContext, { Coordinates } from "../../store/context/game";

const Game: VFC = () => {

  const { data: {
    fieldSize,
    field,
    currentNumber
  }, setData: setGameData } = GameContext.useContext();

  const onCellClick = (row: number, column: number, field: string[][], lastTry: Coordinates) => {
      const fieldCopy = field.slice();

      const nextNumber = (lastTry?.row && lastTry.column) ?
        Number(fieldCopy[lastTry.row!][lastTry.column!]) + 1 :
        1;

      fieldCopy[row][column] = nextNumber.toString();

      console.log(fieldCopy, row, column, currentNumber);

      setGameData({
        field: fieldCopy,
        lastTry: {
          row,
          column,
          number: nextNumber
        }
      });
  };

  useEffect(() => {
    const field = Array(fieldSize.width).fill('0').map(() => Array(fieldSize.height).fill('0'));

    setGameData({
      field,
      onCellClick
    });
  }, [fieldSize]);


  const rows = useCallback(() => field.map((row, index) => (
    <Row key={`row${index}`} content={row} number={index} />))
    , [field])();

  return (
    <section>
      {rows}
    </section>
  )
};

export default Game;
