import { FC, useEffect, useState } from "react";
import StatsContext from "../../store/context/stats";

type TimerProps = {
  isTicking: boolean;
}
const Timer: FC<TimerProps> = ({ isTicking }) => {
  const [time, setTime] = useState(0);

  const { data: { bestGame }, setData } = StatsContext.useContext();

  let timer: NodeJS.Timer;

  useEffect(() => {
    timer = setInterval(() => {
      setTime(prev => prev + 1);
    }, 1000);


    return () => {
      console.log('final time:', time);

      clearInterval(timer);
    }
  }, [isTicking]);

  useEffect(() => {
    setData({
      bestGame: {
        ...bestGame,
        time
      }
    });

  }, [time]);

  return (
    <section>
      <p>time: <span>{time < 10 ? '0' : ''}{time}</span> seconds </p>
    </section>
  )
}

export default Timer;
