import Context from "./context";


export type Size = {
  width: number
  height: number;
}

export type GameStore = {
  moves: number;
  currentNumber: number;
  time: number;
  fieldSize: Size;
  field: string[][];
};

export const initialGameState: GameStore = {
  moves: 0,
  time: 0,
  fieldSize: {
    width: 0,
    height: 0
  },
  field: []
};

const GameContext = new Context(initialGameState);

export default GameContext;
