import { useCallback, useEffect, useMemo, VFC } from "react";

import Row from "../../components/row";

import GameContext from "../../store/context/game";

const Game: VFC = () => {

  const { data: {
    fieldSize,
    field,
    currentNumber
  }, setData: setGameData } = GameContext.useContext();

  const onCellClick = (row: number, column: number, field: string[][]) => {
      const fieldCopy = field.slice();
      console.log(row, column);

      fieldCopy[row][column] = currentNumber.toString();

      console.log(fieldCopy, row, column);

      setGameData({
        field: fieldCopy,
        currentNumber: currentNumber + 1
      });
  };

  useEffect(() => {
    const field = Array(fieldSize.height)
      .fill(Array(fieldSize.width).fill('0'));

    setGameData({
      field,
      onCellClick
    });
  }, [fieldSize]);


  const rows = useCallback(() => field.map((row, index) => (
    <Row key={`row${index}`} content={row} number={index} />))
    , [field])();

  return (
    <section>
      {rows}
    </section>
  )
};

export default Game;
