    <main>
      <Navigation />

      <h1>Profile</h1>

      <section>
        <h3>Your statistics:</h3>

          <article>
            <h4>Total games played: </h4>
            <p>{ stats.totalGames }</p>
          </article>

          <article>
            <h4>Best game : </h4>

            <article>
              <h5>time: </h5>
              <p>{ stats.bestGame.time }</p>
            </article>

            <article>
              <h5>moves: </h5>
              <p>{ stats.moves }</p>
            </article>

            <article>
              <h5>field: </h5>
              <p>{ stats.bestGame.field.width } x { stats.bestGame.field.height }</p>
            </article>
          </article>
      </section>
    </main>
  );
};

