import React, { FormEvent, useEffect, useRef, VFC } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import AuthService from '../../api/services/auth';
import Button from '../../components/button';
import Input from '../../components/input';
import AppContext from '../../store/context/app';

const Login: VFC = () => {
  const emailRef = useRef<HTMLInputElement>(null);
  const PasswordRef = useRef<HTMLInputElement>(null);

  const { data: { isLoading }, setData: setAppData } = AppContext.useContext();

  const navigate = useNavigate();

  const onSubmit = (event: FormEvent) => {
    if (event) event.preventDefault();

    const [
      email,
      password
    ] = [emailRef, PasswordRef].map(ref => ref.current?.value);

    setAppData({ isLoading: true });

    AuthService.login({
      email: email!,
      password: password!
    })
    .then(res => {
      const { token, user } = res.data;

      setAppData({ user, token });

      localStorage.setItem('userId', user._id!);
      localStorage.setItem('AuthToken', token);
    })
    .then(() => navigate('/main'))
    .catch(err => {
      console.log(err);
    })
    .finally(() => setAppData({ isLoading: false }));
  }

  useEffect(() => {
    localStorage.removeItem('userId');
    localStorage.removeItem('AuthToken');
  }, []);

  return (
    <section className="login__contaner">
      <section className="login__content">
        <h2>Login</h2>

        <form onSubmit={onSubmit}>
          <Input name='email' placeholder='Enter login or email' ref={emailRef} />
          <Input name='password' placeholder='Enter password' ref={PasswordRef} />

          <Button disabled={isLoading}>
            Login
          </Button>
        </form>

        <section>
          Not registered?&nbsp;
          <Link to='/register'>Register</Link>
        </section>
      </section>
    </section>
  )
};

export default Login;
