import Context from "./context";


export type Size = {
  width: number
  height: number;
}

export type Try = {
  row: number | null;
  column: number | null;
  number: number;
  moves: number;
}

export type GameStore = {
  currentNumber: number;
  lastTry: Try
  time: number;
  fieldSize: Size;
  field: string[][];
  onCellClick: (row: number, column: number, field: string[][], lastTry: Try) => void;
};

export const initialGameState: GameStore = {
  currentNumber: 1,
  lastTry: {
    row: null,
    column: null,
    number: 0,
    moves: 0,
  },
  time: 0,
  fieldSize: {
    width: 0,
    height: 0
  },
  field: [],
  onCellClick: (row, column, field, lastTry) => {}
};

const GameContext = new Context(initialGameState);

export default GameContext;
