import { useEffect, VFC } from "react";
import { useNavigate } from "react-router";
import StatsService from "../../api/services/stats";
import Navigation from "../../components/navbar";
import AppContext from "../../store/context/app";
import StatsContext from "../../store/context/stats";

const Profile: VFC = () => {
  const { data: { user: { _id: id } } } = AppContext.useContext();
  const { data: {
    totalGames,
    moves,
    bestGame
  }, setData: setStatsData } = StatsContext.useContext();

  const navigate = useNavigate();

  useEffect(() => {
    const userId = id || localStorage.getItem('userId');
    console.log(userId);
    if (userId) {
      StatsService.get(userId)
        .then(res => {
          console.log(res.data);

          setStatsData(res.data);
        })
        .catch((err) => {
          navigate('/login');
        });
    }
  }, [id]);

  return (
    <main>
      <Navigation />

      <h1>Profile</h1>

      <section>
        <h3>Your statistics:</h3>
      </section>
    </main>
  );
};

export default Profile;
