import { memo, useEffect, useState, VFC } from "react";
import { useNavigate } from "react-router";
import StatsService from "../../api/services/stats";
import Navigation from "../../components/navbar";
import AppContext from "../../store/context/app";
import StatsContext, { StatsStore } from "../../store/context/stats";

const Profile: VFC = () => {
  const { data: { user: { _id: id }, token } } = AppContext.useContext();

  const { data: {
    totalGames,
    moves,
    bestGame
  }} = StatsContext.useContext();

  const navigate = useNavigate();

  useEffect(() => {
    const userId = id || localStorage.getItem('userId');
    const authToken = token || localStorage.getItem('AuthToken')

    if (!userId && !authToken) navigate('/login');
  }, [id]);

  return (
    <main>
      <Navigation />

      <h1>Profile</h1>

      <section>
        <h3>Your statistics:</h3>

          <article>
            <h4>Total games played: </h4>
            <p>{ totalGames }</p>
          </article>

          <article>
            <h4>Best game : </h4>

            <article>
              <h5>time: </h5>
              <p>{ bestGame.time }</p>
            </article>

            <article>
              <h5>moves: </h5>
              <p>{ moves }</p>
            </article>

            <article>
              <h5>field: </h5>
              <p>{ bestGame.field.width } x {bestGame.field.height}</p>
            </article>
          </article>
      </section>
    </main>
  );
};

export default memo(Profile);
